<?php$project = $SOUP->get('project');?><script type="text/javascript">$(document).ready(function(){	<?php if( $project->isTrusted(Session::getUserID()) ||				$project->isMember(Session::getUserID()) ): ?>			$('#btnLeave').click(function(){		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info':{				'action': 'leave'			},			'buttonID': '#btnLeave'		});	});							<?php elseif($project->isFollower(Session::getUserID())): ?>		$('#btnUnfollow').click(function(){		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info':{				'action': 'unfollow'			},			'buttonID': '#btnUnfollow'		});	});			<?php elseif(!$project->isAffiliated(Session::getUserID())): ?>		$('#btnFollow').click(function(){		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info':{				'action': 'follow'			},			'buttonID': '#btnFollow'		});	});			<?php endif; ?>		<?php if( !$project->isAffiliated(Session::getUserID()) ||			 $project->isFollower(Session::getUserID()) ): ?>	$('#btnJoin').click(function(){		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info':{				'action': 'join'			},			'buttonID': '#btnJoin'		});	});				<?php endif; ?>	});</script><div class="your-role"><?phpif(!Session::isLoggedIn()) {	echo '<p>You must <a href="'.Url::logIn().'"><strong>log in</strong></a> to contribute.</p>';} elseif($project->isCreator(Session::getUserID())) {	echo '<p>You <strong>created</strong> this project.</p>';} elseif($project->isTrusted(Session::getUserID())) {	echo '<p>You are a <a href="'.Url::help().'"><strong>trusted member</strong></a> of this project.</p>';	echo '<input id="btnLeave" type="button" value="Leave Project" />';} elseif($project->isMember(Session::getUserID())) {	echo '<p>You are a <strong>member</strong> of this project.</p>';	echo '<input id="btnLeave" type="button" value="Leave Project" />';} elseif($project->isFollower(Session::getUserID())) {	echo '<p>You are <strong>following</strong> this project.</p>';	echo '<input id="btnJoin" type="button" value="Join Project" /> <input id="btnUnfollow" type="button" value="Unfollow Project" />';	} elseif($project->isBanned(Session::getUserID())) {	echo '<p>You are <strong>banned</strong> from this project.</p>';//} elseif(Invitation::hasInvitations(Session::getUserID(), $project->getID())) {//	echo '<p>You have <a href="'.Url::dashboard().'">pending invitations</a> for this project.</p>';} elseif(!$project->isAffiliated(Session::getUserID())) {		echo '<p>Like what you see? <strong>Join</strong> this project to contribute or <strong>follow</strong> it to get updates sent to your <a href="'.Url::dashboard().'">dashboard</a>.</p>';	echo '<input id="btnJoin" type="button" value="Join Project" /> <input id="btnFollow" type="button" value="Follow Project" />';}?></div>